
let leaderboardData;window.addEventListener("load",function(){let tokencheck=localStorage.getItem("token");if(!tokencheck){alert("wrong page");window.location.replace("login.html");}
else{fetch("http://localhost:3000/api/v1/leaderboard",{method:"get",headers:{"Content-Type":"application/json",'Authorization':`Bearer ${tokencheck}`}}).then(response=>{console.log("yess");return response.json();}).then(json=>{if(json.status==="Succes"){leaderboardData=json.data;let elementrank=1;json.data.forEach(function(element){let firstname=element.firstname;let lastname=element.lastname;let elementamount=element.coins;let leaderboard=`<li class="list__item">
                            <p class="list__item--ranking">${elementrank}</p>
                            <p class="list__item--amount">${elementamount}P</p>
                            <p class="list__item--from-to">${firstname + " " + lastname}</p>
                        </li>
                        <hr class="list__hr">`
document.querySelector(".list").innerHTML+=leaderboard;elementrank++;});}})}});